import axios from './axios';
import React,{useState,useEffect} from 'react'
import './Banner.css'
import requests from './Request';

function Banner() {
    const [movies, setMovies] = useState([]);
    useEffect(()=>{
        async function fetchData(){
            const request = await axios.get(requests.fetchNetflixOriginals);
        }
    },[])
    function truncate(string,n){
        return string.length > n ? string.substr(0,n-1)+'...':string
    }
    return (
        <header className='banner' style={{
            backgroundSize:'cover',
            backgroundImage:`url('https://upload.wikimedia.org/wikipedia/commons/thumb/c/cd/Black_flag.svg/1200px-Black_flag.svg.png')`,
            backgroundPosition:'center center',
        }}>
            <div className='banner_contents'>
                <h1 className='banner_title'> title here</h1>
                <div className='banner_buttons'>
                    <button className='banner_button'>play</button>
                    <button className='banner_button'>my playlist</button>
                </div>
                <h1 className='banner_description'>
                    {
                        truncate(
                            `While navigating their careers in Los Angeles, a pianist and an actress fall in love while attempting to reconcile their aspirations for the future.`,150
                        )
                    }
                </h1>
            </div>
            <div className='banner_fade_button'></div>
        </header>
    )
}

export default Banner
